import { useState } from "react";
import "tailwindcss/tailwind.css";

const Navbar = () => {
  const [active, setActive] = useState("Home");
  const [toggle, setToggle] = useState(false);

  const navbarStyle = {
    display: "flex",
    flexDirection: "row",
    justifyContent: "center",
    alignItems: "center",
    width: hovered ? "110%" : "100%", // Dynamically adjust width
  };

  const navLinksStyle = {
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-between",
    width: "100%",
    alignSelf: "center",
    alignItems: "center",
    alignContent: "center",
    background: "#fff",
    padding: "20px 15px",
    borderRadius: "4px",
    boxShadow: "0 5px 10px rgba(0, 0, 0, 0.2)",
    marginBottom: "3%",
    flexWrap: "wrap",
    gap: "10px",
    ":hover": {
      boxShadow: "0 0 8px #fd0018, 0 0 35px #fd0018, 0 0 60px #fd0018",
    },
  };

  const linkStyle = {
    width: "20%",
    justifyContent: "left",
    display: "flex",
    marginLeft: "100px",
    flexDirection: "row",
    marginBottom: "10px",
    letterSpacing: "5px",
    gap: "15px",
  };

  const navLinksItemStyle = {
    display: "flex",
    flexDirection: "row",
    listStyle: "none",
    margin: "0 12px",
  };

  const navLinksItemLinkStyle = {
    position: "relative",
    color: "black",
    fontSize: "25px",
    fontFamily: "open_sansregular",
    fontWeight: "bold",
    padding: "6px 0",
    textDecoration: "none",
  };

  const navLinksItemLinkBeforeStyle = {
    content: '""',
    position: "absolute",
    bottom: "0",
    left: "0",
    height: "3px",
    width: "0%",
    background: "red",
    borderRadius: "12px",
    transition: "all 0.4s ease",
  };

  const updateWidth = (width) => {
    navLinksStyle.width = width;
  };

  return (
    <div className="navbarStyle">
      <div className="principalContent">
        <nav style={navbarStyle}>
          <ul className="nav-links" style={navLinksStyle} onMouseOver={() => updateWidth("110%")} onMouseOut={() => updateWidth("100%")}>
            <div className="logo">
              <img src="/logo (1).png" alt="PH NegÃ³cios Plataforma" style={{ width: '100px', height: 'auto', marginTop: '5%', marginBottom: '3%' }} />
              <div className='link'>
                <li style={navLinksItemStyle}><a href="/" style={navLinksItemLinkStyle}>Bases</a></li>
                <li className="center" style={navLinksItemStyle}><a href="#" style={navLinksItemLinkStyle}>GrÃ¡ficos</a></li>
                <li className="upward" style={navLinksItemStyle}><a href="#" style={navLinksItemLinkStyle}>Adicionar</a></li>
                <li className="forward" style={navLinksItemStyle}><a href="#" style={navLinksItemLinkStyle}>Update</a></li>
              </div>
            </div>
          </ul>
        </nav>
      </div>
    </div>
  );
};

export default Navbar;
